import { Component, OnInit } from "@angular/core";
import { ApiService } from "../api.service";
import { ActivatedRoute, Router } from "@angular/router";

@Component({
  selector: "dettaglio",
  templateUrl: "./dettaglio.component.html",
  styleUrls: ["./dettaglio.component.css"],
})
export class Dettaglio implements OnInit {

  baseimg:string = 'https://www.thecocktaildb.com/images/ingredients/'
 
  id: any;
  dati: any;

  ingredients: string[] = [];
  measure: string[] = [];
  immagini: string[] = [];

  constructor(private route: ActivatedRoute, private apiService: ApiService, private router: Router) {}
  ngOnInit() {
    this.route.paramMap.subscribe((params) => {
      this.id = params.get("id");
      this.apiService.getDetail(this.id).subscribe((data) => {
        this.dati = data;

        if (!this.dati || !this.dati.drinks || this.dati.drinks.length === 0) {
          this.router.navigate(['/404-not-found']);
          return;
        }

        console.log(this.dati);

        for (let i = 1; i <= 15; i++) {
          const ingredientKey = `strIngredient${i}`;
          const measureKey = `strMeasure${i}`;
          if (this.dati.drinks[0][ingredientKey]) {
            const img:string = `${this.baseimg}${this.dati.drinks[0][ingredientKey]}${'.png'}`
            this.immagini.push(img);
            this.ingredients.push(this.dati.drinks[0][ingredientKey]);
          }
          if (this.dati.drinks[0][measureKey]) {
            this.measure.push(this.dati.drinks[0][measureKey]);
          }
        }
      });
    });
  }
}

    <div *ngIf="dati">
        <div *ngFor="let drink of dati.drinks" class="d-flex flex-wrap gap-5 px-5">
            <h1 class="w-100 fs-titolo text-white text-uppercase text-center d-flex align-items-center gap-5 justify-content-center">{{ drink.strDrink }}</h1>
                <div class="col-lg-4"><img src={{drink.strDrinkThumb}} class="w-100" alt=""></div>
            <div class="col-lg-7 gap-4 d-flex flex-wrap">
                <h2 class="fs-1 text-uppercase w-100">ingredienti</h2>
                <h5 class="w-100">Clicca su un ingrediente per vedere quanti altri drink si possono fare</h5>
                <ul class="w-35">
                    <li  *ngFor="let img of immagini" class="fs-4 fw-bold">
                        <div >
                            <a routerLink="/ingrediente/{{ ingredient }}" class="text-decoration-underline text-white">{{ingredient}}</a>
                            <img src={{img}} class="w-25" alt="">
                        </div>
                    </li>
                </ul>
                <ul class="list-unstyled w-35">
                    <li *ngFor="let measure of measure" class="fs-4 fw-bold">{{ measure }}</li>
                </ul>
                <h2 class="fs-1 text-uppercase w-100">preparazione</h2>
                <p class="fs-5 fw-semibold">{{drink.strInstructionsIT}}</p>
            </div>
        </div>
    </div>
