import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

interface Drink {}
@Injectable({
  providedIn: 'root',
})
export class ApiService {

  private SearchByFURL =  'https://www.thecocktaildb.com/api/json/v1/1/search.php?f='+'a'; 
  private DetailsURL = 'https://www.thecocktaildb.com/api/json/v1/1/';
  private AnalcoholicURL =  'https://www.thecocktaildb.com/api/json/v1/1/filter.php?a=Non_Alcoholic'; 
  private AlcoholicURL =  'https://www.thecocktaildb.com/api/json/v1/1/filter.php?a=Alcoholic'; 
  private IngredientsURL =  'https://www.thecocktaildb.com/api/json/v1/1/'; 

  constructor(private http: HttpClient) {}

  query:string | undefined
  setLetter(a:string){
    this.query = a
  }


  searchByF() {
    return this.http.get(this.SearchByFURL) as Observable<Drink[]>;
  }
  getDetail(id: string) {
    // Costruisci l'URL dell'API utilizzando l'ID
    const apiUrl = `${this.DetailsURL}lookup.php?i=${id}`;
    return this.http.get(apiUrl);
  }
  getAnalcoholic() {
    return this.http.get(this.AnalcoholicURL);

  }
  getAlcoholic() {
    return this.http.get(this.AlcoholicURL);
  }
  getIngredients(nome: string) {
    // Costruisci l'URL dell'API utilizzando l'ID
    const apiUrl = `${this.IngredientsURL}filter.php?i=${nome}`;
    return this.http.get(apiUrl);
  }
}
