import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api';


@Component({
  selector: 'HomePage',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css']
})

export class HomePage implements OnInit {
  dati: any;

  constructor(private apiService: ApiService) {}

  ngOnInit() {
    this.apiService.getDati().subscribe((data) => {
      this.dati = data;
      console.log(this.dati)
    });
  }
}



<div *ngIf="dati" class="p-5">
    <h1 class="text-center pb-5 w-100 fs-titolo text-white text-uppercase text-center d-flex align-items-center gap-4 justify-content-center">Browse Cocktails</h1>
    <div class="d-flex flex-wrap flex-direction-column gap-5 justify-content-around">
        <a routerLink="/dettaglio/{{drink.idDrink}}" 
            *ngFor="let drink of dati.drinks" 
            class="w-25 bg-white p-3 rounded-2 ombra">
          <div  class="d-flex flex-column gap-2">
              <img src={{drink.strDrinkThumb}} class="img-fluid" alt="">
              <h2 class="text-center text-black">{{ drink.strDrink }}</h2>
            </div>
          </a>
      </div>
</div>
