import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

interface Drink {}
@Injectable({
  providedIn: 'root',
})
export class ApiService {

  private searchByLetterURL = 'https://www.thecocktaildb.com/api/json/v1/1/search.php?f=';
  private DetailsURL = 'https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=';
  private AnalcoholicURL =  'https://www.thecocktaildb.com/api/json/v1/1/filter.php?a=Non_Alcoholic'; 
  private AlcoholicURL =  'https://www.thecocktaildb.com/api/json/v1/1/filter.php?a=Alcoholic'; 
  private IngredientsURL =  'https://www.thecocktaildb.com/api/json/v1/1/filter.php?i='; 

  constructor(private http: HttpClient) {}

  searchByF(letter: string) {
    const searchByLetter = `${this.searchByLetterURL}${letter}`
    return this.http.get(searchByLetter) as Observable<Drink[]>;
  }

  getDetail(id: string) {
    const apiUrl = `${this.DetailsURL}${id}`;
    return this.http.get(apiUrl);
  }
  getAnalcoholic() {
    return this.http.get(this.AnalcoholicURL);

  }
  getAlcoholic() {
    return this.http.get(this.AlcoholicURL);
  }
  getIngredients(nome: string) {
    // Costruisci l'URL dell'API utilizzando l'ID
    const apiUrl = `${this.IngredientsURL}${nome}`;
    return this.http.get(apiUrl);
  }
}
