import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root',
})

export class ApiService {

  private SearchByFURL =  'https://www.thecocktaildb.com/api/json/v1/1/search.php?f=a'; 
  private DetailsURL = 'https://www.thecocktaildb.com/api/json/v1/1/';
  private AnalcoholicURL =  'https://www.thecocktaildb.com/api/json/v1/1/filter.php?a=Non_Alcoholic'; 

  constructor(private http: HttpClient) {}

  searchByF() {
    return this.http.get(this.SearchByFURL);
  }
  getDetail(id: string) {
    // Costruisci l'URL dell'API utilizzando l'ID
    const apiUrl = `${this.DetailsURL}lookup.php?i=${id}`;
    return this.http.get(apiUrl);
  }
  getAnalcoholic() {
    return this.http.get(this.AnalcoholicURL);

  }
  getAnalcoholic() {
    return this.http.get(this.AnalcoholicURL);

  }
}
