import { Component, OnInit } from "@angular/core";
import { ApiDetails } from "../apiDetails";
import { ActivatedRoute } from "@angular/router";

@Component({
  selector: "ingrediente",
  templateUrl: "./ingrediente.component.html",
  styleUrls: ["./dettaglio.component.css"],
})
export class Ingrediente implements OnInit {
  nome: any;
  dati: any;

  ingredients: string[] = [];
  measure: string[] = [];

  constructor(private route: ActivatedRoute, private apiService: ApiDetails) {}
  ngOnInit() {
    this.route.paramMap.subscribe((params) => {
      this.nome = params.get("id");
      this.apiService.getDati(this.nome).subscribe((data) => {
        this.dati = data;
        console.log(this.dati);
      });
    });
  }
}

