import { Component, OnInit } from '@angular/core';
import { ApiAnalcoholic } from '../apiAnalcolici';


@Component({
  selector: 'Analcohlic',
  templateUrl: './analcohlic-drink-page.component.html',
  styleUrls: ['./analcohlic-drink-page.component.css']
})

export class Analcohlic implements OnInit {
  dati: any;

  constructor(private apiService: ApiAnalcoholic) {}

  ngOnInit() {
    this.apiService.getDati().subscribe((data) => {
      this.dati = data;
      console.log(this.dati)
    });
  }
}



<div *ngIf="dati" class="p-5">
    <h1 class="text-center pb-5 w-100 fs-titolo text-white text-uppercase text-center d-flex align-items-center gap-4 justify-content-center">analcolici</h1>
    <div class="d-flex flex-wrap gap-5 justify-content-around">
      <div class="row flex-wrap">
      <a routerLink="/dettaglio/{{drink.idDrink}}" 
          *ngFor="let drink of dati.drinks" 
          class="p-4  col-4">
          <div  class="d-flex flex-column gap-2 position-relative col-12 overflow-hidden rounded-2 ombra">
            <div class="overlay position-absolute overflow-hidden d-flex align-items-center justify-content-center text-white pt-3 text-uppercase fw-bold fs-2 text-center">{{drink.strDrink}}</div>
              <img src={{drink.strDrinkThumb}} class="img-fluid" alt="">
            </div>
          </a>
        </div>
    </div>
</div>
